CREATE   OR   REPLACE   FUNCTION   dt_trigger_func ()  RETURNS   TRIGGER   AS   $$  BEGIN 
     IF   ( TG_OP   =   INSERT )   THEN 
         NEW . creation_date   : =   now (); 
     ELSIF   ( TG_OP   =   UPDATE )   THEN 
         NEW . modification_date   : =   now (); 
     END   IF ; 
     RETURN   NEW ;  END  $$   LANGUAGE   plpgsql ;

CREATE   OR   REPLACE   FUNCTION   delete_tbl_trigger_fn ()  RETURNS   trigger   AS   $$  BEGIN 
     UPDATE   tbl   SET   deleted = true   WHERE   id = OLD . id ; 
     RETURN   NULL ;   -- Note RETURN NULL, not RETURN OLD!  END ;  $$   LANGUAGE   plpgsql ;  CREATE   TRIGGER   delete_tbl_trigger  BEFORE   DELETE  ON   tbl  FOR   EACH   ROW  EXECUTE   PROCEDURE   delete_tbl_trigger_fn ();

CREATE   OR   REPLACE   function   increment_salaer  (   v_salaer   IN   Firma . SalaerSumme % TYPE )  RETURNS   null  AS   $$  BEGIN 
     UPDATE   Firma 
     SET   SalaerSumme   =   coalesce   ( SalaerSumme   , 0 )   +   v_salaer ;  END   ;  $$   language   plpgsql ;  CREATE   OR   REPLACE   FUNCTION   berechne_SalSumme_trigger_fn ()  RETURNS   trigger   AS   $$  BEGIN 
     IF   TG_OP = DELETE   THEN   PERFORM   increment_salaer (   - 1   *   OLD . salaer ); 
     ELSIF   TG_OP = UPDATE   THEN 
         PERFORM   increment_salaer ( NEW . Salaer - OLD . Salaer ); 
     ELSE   /*inserting*/   PERFORM   increment_salaer ( NEW . salaer ); 
     END   IF ; 
     RETURN   null ;  END ;  $$   LANGUAGE   plpgsql ;  CREATE   TRIGGER   berechne_Salaersumme  AFTER   INSERT   OR   UPDATE   OF   Salaer   OR   DELETE   ON   Angestellter  FOR   EACH   ROW  EXECUTE   PROCEDURE   berechne_SalSumme_trigger_fn ();

CREATE   OR   REPLACE   FUNCTION   process_ang_audit ()   RETURNS  TRIGGER   AS   $$  BEGIN 
     IF   ( TG_OP   =   DELETE )   THEN 
         INSERT   INTO   ang_audit 
             SELECT   D ,   now (),   user ,   OLD . name ,   OLD . salaer ; 
         RETURN   OLD ; 
     ELSIF   ( TG_OP   =   UPDATE )   THEN 
         INSERT   INTO   ang_audit 
             SELECT   U ,   now (),   user ,   NEW . name ,   NEW . salaer ; 
     ELSIF   ( TG_OP   =   INSERT )   THEN 
         INSERT   INTO   ang_audit 
             SELECT   I ,   now (),   user ,   NEW . name ,   NEW . salaer ; 
     END   IF ; 
     RETURN   NULL ; 
     -- result is ignored since this is an AFTER trigger  END ;  $$   language   plpgsql ;

